mapper.select:
SELECT d.doctor_id AS doctorId, d.user_id AS userId, d.dept_id AS deptId, dep.dept_name AS deptName, d.title, d.bio, d.status AS doctorStatus, d.created_at AS createdAt, d.updated_at AS updatedAt, u.username, u.phone, u.email, u.gender, u.status AS userStatus FROM doctors d INNER JOIN users u ON d.user_id = u.user_id LEFT JOIN departments dep ON d.dept_id = dep.dept_id
AND d.dept_id = #{deptId}
AND (u.username LIKE CONCAT('%', #{username}, '%') OR CAST(d.doctor_id AS CHAR) = #{username})
AND u.status = #{status} AND d.status = #{doctorStatus}
ORDER BY d.created_at DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(1)
FROM doctors d
INNER JOIN users u ON d.user_id = u.user_id
LEFT JOIN departments dep ON d.dept_id = dep.dept_id

     AND d.dept_id = #{deptId} 

        AND (u.username LIKE CONCAT('%', #{username}, '%') OR CAST(d.doctor_id AS CHAR) = #{username})

     AND u.status = #{status} 
     AND d.status = #{doctorStatus}
